scene.onOverlapTile(SpriteKind.Player, sprites.vehicle.roadVertical, function (sprite, location) {
    if (ayuda == "S") {
        tiles.setTileAt(tiles.getTileLocation(0, 0), myTiles.tile1)
    }
})
scene.onOverlapTile(SpriteKind.Player, sprites.vehicle.roadHorizontal, function (sprite, location) {
    if (ayuda == "S") {
        tiles.setTileAt(tiles.getTileLocation(0, 0), myTiles.tile2)
    }
})
function Fondos () {
    color = randint(1, 16)
    if (color == 1) {
        scene.setBackgroundColor(0)
    } else if (color == 2) {
        scene.setBackgroundColor(1)
    } else if (color == 3) {
        scene.setBackgroundColor(2)
    } else if (color == 4) {
        scene.setBackgroundColor(3)
    } else if (color == 5) {
        scene.setBackgroundColor(4)
    } else if (color == 6) {
        scene.setBackgroundColor(5)
    } else if (color == 7) {
        scene.setBackgroundColor(6)
    } else if (color == 8) {
        scene.setBackgroundColor(7)
    } else if (color == 9) {
        scene.setBackgroundColor(8)
    } else if (color == 10) {
        scene.setBackgroundColor(9)
    } else if (color == 11) {
        scene.setBackgroundColor(10)
    } else if (color == 12) {
        scene.setBackgroundColor(11)
    } else if (color == 13) {
        scene.setBackgroundColor(12)
    } else if (color == 14) {
        scene.setBackgroundColor(13)
    } else if (color == 15) {
        scene.setBackgroundColor(14)
    } else if (color == 16) {
        scene.setBackgroundColor(15)
    }
}
